/**
* @private
*/
class TreeIterator {
  constructor (set, type) {
    const node = set._tree
    const keys = Object.keys(node)
    this.set = set
    this.type = type
    this.path = keys.length > 0 ? [{ node, keys }] : []
  }

  next () {
    const value = this.dive()
    this.backtrack()
    return value
  }

  dive () {
    if (this.path.length === 0) { return { done: true } }
    const { node, keys } = last(this.path)
    if (last(keys) === LEAF) { return { done: false, value: this.result() } }
    this.path.push({ node: node[last(keys)], keys: Object.keys(node[last(keys)]) })
    return this.dive()
  }

  backtrack () {
    if (this.path.length === 0) { return }
    last(this.path).keys.pop()
    if (last(this.path).keys.length > 0) { return }
    this.path.pop()
    this.backtrack()
  }

  key () {
    return this.set._prefix + this.path
      .map(({ keys }) => last(keys))
      .filter(key => key !== LEAF)
      .join('')
  }

  value () {
    return last(this.path).node[LEAF]
  }

  result () {
    if (this.type === VALUES) { return this.value() }
    if (this.type === KEYS) { return this.key() }
    return [this.key(), this.value()]
  }

  [Symbol.iterator] () {
    return this
  }
}

/** @ignore */
const ENTRIES = 'ENTRIES'

/** @ignore */
const KEYS = 'KEYS'

/** @ignore */
const VALUES = 'VALUES'

/** @ignore */
const LEAF = ''

const last = function (array) {
  return array[array.length - 1]
}

export { TreeIterator, ENTRIES, KEYS, VALUES, LEAF }

import { LEAF } from './TreeIterator.js'

/**
* @ignore
*/
export const fuzzySearch = function (node, query, maxDistance) {
  const stack = [{ distance: 0, i: 0, key: '', node }]
  const results = {}
  while (stack.length > 0) {
    const { node, distance, key, i, edit } = stack.pop()
    Object.keys(node).forEach(k => {
      if (k === LEAF) {
        const totDistance = distance + (query.length - i)
        const [, d] = results[key] || [null, Infinity]
        if (totDistance <= maxDistance && totDistance < d) {
          results[key] = [node[k], totDistance]
        }
      } else {
        withinDistance(query, k, maxDistance - distance, i, edit).forEach(({ distance: d, i, edit }) => {
          stack.push({ node: node[k], distance: distance + d, key: key + k, i, edit })
        })
      }
    })
  }
  return results
}

/**
* @ignore
*/
export const withinDistance = function (a, b, maxDistance, i, edit) {
  const stack = [{ distance: 0, ia: i, ib: 0, edit }]
  const mem = []
  const results = []
  while (stack.length > 0) {
    const { distance, ia, ib, edit } = stack.pop()
    mem[ia] = mem[ia] || []
    if (mem[ia][ib] && mem[ia][ib] <= distance) { continue }
    mem[ia][ib] = distance
    if (ib === b.length) {
      results.push({ distance, i: ia, edit })
      continue
    }
    if (a[ia] === b[ib]) {
      stack.push({ distance, ia: ia + 1, ib: ib + 1, edit: NONE })
    } else {
      if (distance >= maxDistance) { continue }
      if (edit !== ADD) { stack.push({ distance: distance + 1, ia, ib: ib + 1, edit: DELETE }) }
      if (ia < a.length) {
        if (edit !== DELETE) { stack.push({ distance: distance + 1, ia: ia + 1, ib, edit: ADD }) }
        if (edit !== DELETE && edit !== ADD) { stack.push({ distance: distance + 1, ia: ia + 1, ib: ib + 1, edit: CHANGE }) }
      }
    }
  }
  return results
}

const NONE = 0
const CHANGE = 1
const ADD = 2
const DELETE = 3

export default fuzzySearch
